mapper.select:
		SELECT
			*
		FROM
			t_sec_user_role
		where 1=1 

             and name=#{param.name}



             and status=#{param.status}

		ORDER BY
			id
		LIMIT #{offset}, #{limit}

mapper.update:

UPDATE t_sec_user_role SET 
     ID = #{id},
     USER_ID = #{userId},
     ROLE_ID = #{roleId}
WHERE id = #{id};

mapper.select:

	select count(id) from t_sec_user_role 
	where 1=1 

         and name=#{param.name}


         and status=#{param.status}
